import React, { useState } from "react";
import Container from "../Container";
import FormInput from "../form/FormInput";
import Submit from "../form/Submit";
import Title from '../form/Title';
import CustomLink from "../CustomLink";
import { commonModalClasses } from "../util/theme";
import FormContainer from "../form/FormContainer";

export default function Signup() {

  const [userInfo, setUserInfo] = useState({
    name: '',
    email: '',
    password: ''
  });

  const { name, email, password} = userInfo;

  const handleChange = (e) => {
   const target =  e.target;
   console.log(target);
  }

  return (
    <FormContainer>
      <Container>
        <form className={commonModalClasses + " w-72"}>
          <Title>
            Sign up
          </Title>
          <FormInput onChange={handleChange} value={name} label='Name' placeholder='Dylan' name='name' />
          <FormInput onChange={handleChange} value={email} label='Email' placeholder='john@email.com' name='email' />

          <FormInput onChange={handleChange} value={password} label='Password' placeholder='*******' name='password' type='password' />
          <Submit value='Sign up' />
          <div className="flex justify-between">
            <CustomLink to="/auth/forget-password" >Forget Password</CustomLink>
            <CustomLink to="/auth/signin">Sign in</CustomLink>
          </div>
        </form>
      </Container>
    </FormContainer>
  );
}
